import React from 'react';
import MenuBar from "./components/menuBar"
import CatPosts from "./components/catPosts"
import Banner from "./components/banner"
import ReviewsRecentes from './components/reviewsR'
import Modal from '@material-ui/core/Modal';
import EditorTexto from './components/editorTexto';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    modal: {
      position: 'absolute',
      width: "600px",
      height: "600px",
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      marginLeft:"28%",
      marginTop:"2%",
      overflowY:"scroll"
    },
    editorTitulo:{
      marginTop:"5%"
    }
  }));
export default function App() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [id, setId] = React.useState(1);
  const [salvo,setSalvo] = React.useState([false])
  const [postagemInfo, setPostagemInfo] = React.useState([]);
  const [textoSalvo, setTextoSalvo] = React.useState([{id:1,salvo:false}]);
  const [vetorSecoes, setVetorSecoes] = React.useState([
    <EditorTexto id={id} salvar={salvarPostagem}/>
  ])

  function handleOpen(){
    setOpen(true);
  }
  function handleClose(){
    setOpen(false);
  }

  function enviar(){
    console.log(postagemInfo)
  }

  function salvarPostagem(titulo,texto,id){
    console.log(titulo)
    console.log(texto)
    let vetAux = [...postagemInfo]
    vetAux.push({titulo:titulo,texto:texto})
    setPostagemInfo(vetAux)
    /*for(let t = 0; t<vetAux.length;t++){
      if(vetAux[t].id == ident){
        if(vetAux[t].salvo!=true){
          vetAux[t].salvo = true
          postagemAux.push({titulo:titulo,texto:texto})
        }
      }
    }
    setTextoSalvo(vetAux)
    setPostagemInfo(postagemAux)*/
}
  function novaSecao(){
    let i = [...salvo]
    i.push(false)
    setSalvo(i)
    let vetAux = [...vetorSecoes]
    vetAux.push(<EditorTexto id={id+1} salvar={salvarPostagem}/>)
    setId(id+1)
    setVetorSecoes(vetAux)
  }

  return (

    <div className='body'>
      <Banner/>
      <MenuBar/>
      <div className="page">
        <div style={{marginTop:"5%"}}><CatPosts/></div> 
          <ReviewsRecentes openModal={handleOpen}/>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div className={classes.modal}>
          {vetorSecoes.map((secao,key)=>(
            <div key={key}>
              {secao}
          </div>
          ))}
          <button onClick={()=>novaSecao()}>Nova Secao</button>
          <button onClick={()=>enviar()}>Enviar</button>
        </div>
      </Modal>
    </div>
  );
}
