import React from 'react';
import MenuBar from "./components/menuBar"
import CatPosts from "./components/catPosts"
import Banner from "./components/banner"
import ReviewsRecentes from './components/reviewsR'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import {Editor, EditorState, RichUtils} from 'draft-js';
import 'draft-js/dist/Draft.css';

const useStyles = makeStyles((theme) => ({
  modal: {
    position: 'absolute',
    width: "600px",
    height: "600px",
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    marginLeft:"28%",
    marginTop:"2%"
  },
  editorTitulo:{
    marginTop:"5%"
  }
}));
export default function App() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [editorState, setEditorState] = React.useState(
    () => EditorState.createEmpty(),
  );
  const editor = React.useRef(null)
  function focusEditor(){
    editor.current.focus()
  }
  function handleKeyCommand(command, editorState) {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState)
      return 'handled';
    }
    return 'not-handled';
  }
  function _onBoldClick() {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'BOLD'));
  }
  function _onItalicClick() {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'ITALIC'));
  }

  function handleOpen(){
    setOpen(true);

  }
  function handleClose(){
    setOpen(false);

  }
  React.useEffect(() => {
    focusEditor()
  }, []);


  const body = (
    <div className={classes.modal}>
      <div className={classes.editorTitulo}>
      <button onClick={_onBoldClick.bind(this)}>Bold</button>
      <button onClick={_onItalicClick.bind(this)}>Italico</button>
        <Editor
          ref={editor} 
          editorState={editorState}
          onChange={setEditorState} 
          handleKeyCommand={handleKeyCommand}/>
      </div>
      <button onClick={()=>console.log(editorState)}>Confirmar</button>
    </div>
  );
  return (

    <div className='body'>
      <Banner/>
      <MenuBar/>
      <div className="page">
        <div style={{marginTop:"5%"}}><CatPosts/></div> 
          <ReviewsRecentes openModal={handleOpen}/>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
