import React from 'react'
import MenuBar from "../components/menuBar"
import Banner from "../components/banner"
import firebase from "../firebase"
import { Link } from "react-router-dom"
import "./styles.css"

export default function PostCatView(){

    const [postId, setPostId] = React.useState(null);
    const [post, setPost] = React.useState({images:'', textos:'', titulos:''});
    const [vetor,setVetor] = React.useState([])
    const [obras, setObras] = React.useState([])
    const [cat, setCat] = React.useState()

    async function carregarDados(){
        let str = '';
        for(var i =0; i<window.location.search.length;i++){
            if(i>3){
                str = `${str}` + `${window.location.search[i]}`
            }
        }
        setCat(str)
        let obrasAux = []; 
        let cont = 0;
        await firebase.database().ref(`posts/categorias/${str.toLowerCase()}`).once('value').
        then(function(snapshot){
            Object.keys(snapshot.val()).forEach(function(postFeed){
                cont++
                obrasAux.push({nomeDaObra:postFeed,images:snapshot.val()[postFeed].fotos,
                    titulos:snapshot.val()[postFeed].titulos,textos:snapshot.val()[postFeed].textos,
                    imagem:snapshot.val()[postFeed].imagem,sinopse:snapshot.val()[postFeed].sinopse,id:snapshot.val()[postFeed].id})
            })


        })
        console.log(cont)
        setObras(obrasAux)
        setPost({titulos:'',images:'',textos:''})
        setPostId(str)
        setVetor([])
    }

    React.useEffect(()=>{
        carregarDados();
    },[])

    function handleClick(id) {
        window.location = `/postView?id=${id}`
    }

    /*function verMais() {
        let vetorAux = [];
        let cont = contador
        let aux = 0
        let i = 0
        for(let y = 0; y<vetorCats.length; y++){
            vetorAux.push(vetorCats[y])
        }
        if(vetorStorage.length - cont < 4){
            cont--
            aux = vetorStorage.length - cont
            for(let t=0; t<aux;t++){
                vetorAux.push(vetorStorage[cont])
                cont++
            }
        }
        for(cont; cont<vetorStorage.length;cont++){
            i++
            if(i<4){
                vetorAux.push(vetorStorage[cont])
            }
        }
        aux != 0 ?  setContador(contador+aux) : setContador(contador+4)
        setVetorCats(vetorAux)
    }*/

    return(
        <div>
            <Banner/>
            <MenuBar/>
            <div className='containerReviews'>
            <h1>{cat}</h1>
            {obras.map((cat, key) => (           
                <div key={key}>
                    <div className="linha"></div>
                    <div className='card' >
                        <Link to={{pathname:'/postView', state:{post:cat}}} ><img className="cardImage" alt='imagem do card' src={cat.imagem} />  </Link>             
                        <div style={{overflowY:"scroll"}}>
                            <div>
                                <Link to={{pathname:'/postView', state:{post:cat}}}><h2>{cat.nomeDaObra}</h2></Link>
                                <h4>{cat.sinopse}</h4>
                            </div>
                        </div>                      
                 </div>
                </div>
            ))}
            <div className='verMaisContainer' style={{ cursor: 'pointer' }} >
                <h1>Ver mais</h1>
            </div>
        </div>
        </div>
    );
}
