import React from 'react';
import MenuBar from "./components/menuBar"
import CatPosts from "./components/catPosts"
import Banner from "./components/banner"
import ReviewsRecentes from './components/reviewsR'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import {Editor, EditorState, RichUtils, convertToRaw} from 'draft-js';
import 'draft-js/dist/Draft.css';
import ImageUploadComponent from "./components/imageUpload"

const useStyles = makeStyles((theme) => ({
  modal: {
    position: 'absolute',
    width: "600px",
    height: "600px",
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    marginLeft:"28%",
    marginTop:"2%"
  },
  editorTitulo:{
    marginTop:"5%"
  }
}));
export default function App() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [editorState, setEditorState] = React.useState(
    () => EditorState.createEmpty(),
  );

  const blocks = convertToRaw(editorState.getCurrentContent()).blocks;
  const tituloValue = blocks.map(block => (!block.text.trim() && '\n') || block.text).join('\n');

  function handleKeyCommand(command, editorState) {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState)
      return 'handled';
    }
    return 'not-handled';
  }
  function _onBoldClick() {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'BOLD'));
  }
  function _onItalicClick() {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'ITALIC'));
  }
  function handleOpen(){
    setOpen(true);
  }
  function handleClose(){
    setOpen(false);
  }
  function criarPostagem(){
    console.log(tituloValue)
  }

  const body = (
    <div className={classes.modal}>
      <div className={classes.editorTitulo}>
        <button onClick={_onBoldClick.bind(this)}>Bold</button>
        <button onClick={_onItalicClick.bind(this)}>Italico</button>
        <div style={{width:"90%",marginLeft:"10%"}} className="editor-container">
          <Editor
            placeholder="Titulo"
            editorState={editorState}
            onChange={setEditorState} 
            handleKeyCommand={handleKeyCommand}/>
        </div>
      </div>
      <button onClick={()=>criarPostagem()}>Confirmar</button>
    </div>
  );
  return (

    <div className='body'>
      <Banner/>
      <MenuBar/>
      <div className="page">
        <div style={{marginTop:"5%"}}><CatPosts/></div> 
          <ReviewsRecentes openModal={handleOpen}/>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
