import React from 'react'
import MenuBar from "../components/menuBar"
import imagem from "../assets/WhatsApp Image 2020-05-29 at 17.22.47.jpeg"
import Banner from "../components/banner"
import firebase from "../firebase"
import "./styles.css"

export default function PostView(){

    const [postId, setPostId] = React.useState(null);
    const [post, setPost] = React.useState({images:'', textos:'', titulos:''});

    async function carregarDados(){
        let str = '';
        for(var i =0; i<window.location.search.length;i++){
            if(i>3){
                str = `${str}` + `${window.location.search[i]}`
            }
        }
        let fotos = []
        let textos = []
        let titulos = []
        await firebase.database().ref(`posts/postsFront/${str}`).once('value').then(function(snapshot){
            fotos = snapshot.val().fotos
            textos = snapshot.val().textos
            titulos = snapshot.val().titulos
        })
        setPost({images:fotos,textos:textos,titulos:titulos})
        setPostId(str)
    }

    React.useEffect(()=>{
        carregarDados();
    },[])

    return(
        <div>
            <Banner/>
            <MenuBar/>
            <div className="containerPost">
                {Object.keys(post).forEach(function(postInd){
                    <>
                    <div className="containerTitle"><h1>{postInd.titulos}</h1></div>
                    <div className="containerImage"><img src={postInd.images}/></div>
                    <div className="containerTexto"><p>{postInd.textos}</p></div>
                    </>
                })}
            </div>
        </div>
    );
}
