import React from 'react';
import MenuBar from "./components/menuBar"
import CatPosts from "./components/catPosts"
import Banner from "./components/banner"
import ReviewsRecentes from './components/reviewsR'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import {Editor, EditorState, RichUtils, convertToRaw} from 'draft-js';
import 'draft-js/dist/Draft.css';
import ImageUploadComponent from "./components/imageUpload"

const useStyles = makeStyles((theme) => ({
  modal: {
    position: 'absolute',
    width: "600px",
    height: "600px",
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    marginLeft:"28%",
    marginTop:"2%",
    overflowY:"scroll"
  },
  editorTitulo:{
    marginTop:"5%"
  }
}));
export default function App() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [editorState, setEditorState] = React.useState(
    () => EditorState.createEmpty(),
  );
  const [vetorEstados, setVetorEstados] = React.useState([
    [editorNovaSecao,setEditorNovaSecao] = React.useState(null),

  ])
  const [editorStateTexto, setEditorStateTexto] = React.useState(
    () => EditorState.createEmpty(),
  );

  const blocksTitulo = convertToRaw(editorState.getCurrentContent()).blocks;
  const tituloValue = blocksTitulo.map(block => (!block.text.trim() && '\n') || block.text).join('\n');
  const blocksTexto = convertToRaw(editorStateTexto.getCurrentContent()).blocks;
  const textoValue = blocksTexto.map(block => (!block.text.trim() && '\n') || block.text).join('\n');

  function toggleInlineStyle(inlineStyle) {
    setEditorStateTexto(RichUtils.toggleInlineStyle(editorStateTexto, inlineStyle));
  }
  function toggleBlockTypeStyle(blockType) {
    setEditorState(RichUtils.toggleBlockType(editorState, blockType));
  }

  function novaSecao(){
    let vetAux = [...vetorEstados]
    console.log(vetAux)
  }
  function criarPostagem(){
    console.log("titulo e texto da primeira secao")
    console.log(tituloValue)
    console.log(textoValue)
  }
  const [body, setBody] = React.useState([
    <div>
      <div className={classes.editorTitulo}>
        <div>
          <button onClick={()=>toggleBlockTypeStyle("header-one")}>Titulo Grande</button>
          <button onClick={()=>toggleBlockTypeStyle("header-three")}>Titulo Medio</button>
          <button onClick={()=>toggleBlockTypeStyle("header-five")}>Titulo Pequeno</button>
        </div>
        <div style={{width:"90%",marginLeft:"10%"}} className="editor-container">
          <Editor
            placeholder="Titulo"
            editorState={editorState}
            onChange={setEditorState}/>
        </div>
        <div>
          <ImageUploadComponent/>
        </div>
        <div>
          <button onClick={()=>toggleInlineStyle("BOLD")}>Bold</button>
          <button onClick={()=>toggleInlineStyle("ITALIC")}>Italico</button>
          <button onClick={()=>toggleInlineStyle("UNDERLINE")}>Sublinhar</button>
          <Editor
            placeholder="Texto"
            editorState={editorStateTexto}
            onChange={setEditorStateTexto} />
        </div>
      </div>
      <button onClick={()=>novaSecao()}>Nova Secao</button>
      <button onClick={()=>criarPostagem()}>Enviar</button>
    </div>]
  );
  function handleOpen(){
    setOpen(true);
  }
  function handleClose(){
    setOpen(false);
  }
  return (

    <div className='body'>
      <Banner/>
      <MenuBar/>
      <div className="page">
        <div style={{marginTop:"5%"}}><CatPosts/></div> 
          <ReviewsRecentes openModal={handleOpen}/>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div className={classes.modal}>{body.map((secao)=>(
          <div>
            {secao}
          </div>
        ))}</div>
      </Modal>
    </div>
  );
}
