import React from 'react'
import MenuBar from "../components/menuBar"
import imagem from "../assets/WhatsApp Image 2020-05-29 at 17.22.47.jpeg"
import "./styles.css"

export default function PostView(){

    const [postId, setPostId] = React.useState(null);
    const [post, setPost] = React.useState({image:imagem, texto:"texto", titulo:"Minecraft Dungeons é uma aventura charmosa e cativante"});

    function carregarDados(){
        let str = '';
        for(var i =0; i<window.location.search.length;i++){
            if(i>3){
                str = `${str}` + `${window.location.search[i]}`
            }
        }
        setPostId(str)
    }

    React.useEffect(()=>{
        carregarDados();
    },[])

    return(
        <div>
            <MenuBar/>
            <div className="containerPost">
                <h1>{post.titulo}</h1>
                <div style={{height:"400px",width:"400px"}}><img className="image" src={post.image}/></div>
            </div>
        </div>
    );
}
