import React from 'react';
import MenuBar from "./components/menuBar"
import CatPosts from "./components/catPosts"
import Banner from "./components/banner"
import ReviewsRecentes from './components/reviewsR'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import {Editor, EditorState} from 'draft-js';
import 'draft-js/dist/Draft.css';

const useStyles = makeStyles((theme) => ({
  modal: {
    position: 'absolute',
    width: "600px",
    height: "600px",
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    marginLeft:"28%",
    marginTop:"2%"
  },
  editorTitulo:{
    marginTop:"5%"
  }
}));
export default function App() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [editorState, setEditorState] = React.useState(
    () => EditorState.createEmpty(),
  );
  const body = (
    <div className={classes.modal}>
      <div className={classes.editorTitulo}><Editor editorState={editorState} onChange={setEditorState} /></div>
      <button onClick={()=>console.log(editorState)}>Confirmar</button>
    </div>
  );

  function handleOpen(){
    setOpen(true);

  }
  function handleClose(){
    setOpen(false);

  }

  return (

    <div className='body'>
      <Banner/>
      <MenuBar/>
      <div className="page">
        <div style={{marginTop:"5%"}}><CatPosts/></div> 
          <ReviewsRecentes openModal={handleOpen}/>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
