import React from 'react';
import MenuBar from "./components/menuBar"
import CatPosts from "./components/catPosts"
import Banner from "./components/banner"
import ReviewsRecentes from './components/reviewsR'
import Modal from '@material-ui/core/Modal';
import EditorTexto from './components/editorTexto';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    modal: {
      position: 'absolute',
      width: "600px",
      height: "600px",
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      marginLeft:"28%",
      marginTop:"2%",
      overflowY:"scroll"
    },
    editorTitulo:{
      marginTop:"5%"
    }
  }));
export default function App() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [postagemInfo, setPostagemInfo] = React.useState([]);
  const [vetorSecoes, setVetorSecoes] = React.useState([
    <EditorTexto postagemInfo={postagemInfo}  salvar={salvarPostagem}/>
  ])

  function handleOpen(){
    setOpen(true);
  }
  function handleClose(){
    setOpen(false);
  }

  function enviar(){
    if(postagemInfo.length==0){
      alert("Escreva algo")
    }
  }

  function salvarPostagem(postagem,titulo,texto){

      let vetAux = [...postagem]
      vetAux.push({titulo:titulo,texto:texto})
      setPostagemInfo(vetAux)
      console.log(vetAux)
      let vetAux2 = [...vetorSecoes]
      vetAux2.push(<EditorTexto postagemInfo={vetAux} salvar={salvarPostagem}/>)
      setVetorSecoes(vetAux2)

  }

  return (

    <div className='body'>
      <Banner/>
      <MenuBar/>
      <div className="page">
        <div style={{marginTop:"5%"}}><CatPosts/></div> 
          <ReviewsRecentes openModal={handleOpen}/>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div className={classes.modal}>
          {vetorSecoes.map((secao,key)=>(
            <div key={key}>
              {secao}
          </div>
          ))}
          <button onClick={()=>enviar()}>Enviar</button>
        </div>
      </Modal>
    </div>
  );
}
