    const [url, setUrl] = React.useState('')

    function handleChange(e){
        if(e.target.files[0]){
            const {imagem} = image
            setImage(imagem)
        }
    }

    return(
        <div>
            <input type="file" onChange={handleChange}/>
            <button>Enviar</button>
        </div>
    )
}
