import React from 'react'
import firebase from "../firebase"

export default function ImageUploadComponent(props){
    const [image, setImage] = React.useState(null)
    const [url, setUrl] = React.useState('')
    const [progress, setProgress] = React.useState(0)

    function handleChange(e){
        if(e.target.files[0]){
            const imagem = e.target.files[0]
            setImage(imagem)
            console.log(imagem)
        }
    }
    function handleUpload(){
       const uploadTask = firebase.storage().ref(`${image.name}`).put(image)
       uploadTask.on('state_changed',(snapshot)=>{
            const progress = Math.round((snapshot.bytesTransferred/snapshot.totalBytes)*100)
            setProgress(progress)
        },(error)=>{
           console.log(error)
       },()=>{
           firebase.storage().ref().child(image.name).getDownloadURL().then(url=>setUrl(url))
       })
    }

    return(
        <div>
            <progress value={progress} max="100"/>
            <input type="file" onChange={handleChange}/>
            <button onClick={()=>props.sendImage(image,url)}>Enviar</button>
            <br/>
            <img src={url} alt="Uploaded Image" height ="300px" width="400px"/>

        </div>
    )
}
